import React from 'react';
import {Link} from 'react-router-dom';

const characterCard = (props) => {

  let linkButton = null
  if (props.showLink) {
   linkButton = <Link to={{ pathname: '/character/' + props.id }} className="btn btn-primary">View</Link>;
  }

  return <div className="card-body">
            <h5 className="card-title">{props.name}</h5><br/>
            <img src={props.image}></img><br/>
            <hr />
            <b>Specifes:</b>{props.species}<br/>
            <b>Status:</b>{props.status}<br/>
            <b>Gender:</b>{props.gender}<br/>
            <b>Location:</b>{props.location}<br/>
            <b>Place of origin:</b>{props.origin}<br/>
            {linkButton}
            <hr />

   </div>
}
export default characterCard;
